mapper.select:
SELECT * 
FROM member m, community_board c 
WHERE m.m_id=c.m_id AND cb_id=#{pageNo}

SELECT name, cbr_content, reg_date
FROM member m, community_board_reply cbr
WHERE m.m_id = cbr.m_id and cb_id = #{pageNo}

mapper.update:
UPDATE community_board
SET hit = hit + 1
WHERE cb_id = #{pageNo}
