mapper.select:
SELECT * 
FROM community_board c 
WHERE cb_id=#{pageNo}

mapper.update:
UPDATE community_board
SET hit = hit + 1
WHERE cb_id = #{pageNo}

mapper.select:
SELECT COUNT(*)
FROM community_board_good
WHERE cb_id = #{cb_id}

mapper.insert:
INSERT INTO community_board_good (cb_id, m_id)
SELECT #{cb_id}, #{m_id}
WHERE NOT EXISTS (SELECT m_id FROM community_board_good WHERE cb_id = #{cb_id} AND m_id = #{m_id})
