mapper.select:
SELECT vaai.*, aas.*  
FROM v_abandoned_animal_info vaai, abandoned_animal_shelter aas
WHERE desertion_no = #{desertion_no} AND vaai.aas_id = aas.aas_id;

mapper.update:
UPDATE abandoned_animal_info
SET hit = hit + 1
WHERE desertion_no = #{desertion_no};

mapper.select:
